<?phpnamespace Magnezit\Helpers\File;use Bitrix\Main\Application;class Asset{    /**     * @param $sPath     * @return string     */	public static function obtainTimestamp($sPath)	{		$sDocRoot = Application::getDocumentRoot();		$filePath = $sDocRoot.$sPath;		if(file_exists($filePath) && ($mtime = filemtime($filePath)))		{			$sPath .= "?{$mtime}";		}		return $sPath;	}    /**     * @param string $filename [no .php]     * @param array $params     */    public static function includeFile($filename, $params = []) {        global $APPLICATION;        $APPLICATION->IncludeComponent(            "bitrix:main.include",            "",            array_merge(                $params,                array(                    "AREA_FILE_SHOW" => "file",                    "PATH" => "/local/include/" . $filename . ".php",                    'COMPOSITE_FRAME_TYPE' => 'AUTO',                    'COMPONENT_TEMPLATE' => '.default',                    'COMPOSITE_FRAME_MODE' => 'A',                )            ),            false        );    }}